$(function(){var a=$(window).height();$(".top-box").css("height",a-$(".main-footer").outerHeight()-$(".bottom-box").outerHeight()-125);$(".img-head").on("click",function(){var b=$(this);var c=b.prev("input[type=checkbox]");if(c.attr("checked")){c.attr("checked",false)}else{c.attr("checked",true)}b.parent().toggleClass("active")});$(".head-check-item .fa-square-o").click(function(){var b=$(this);b.parent().addClass("active");b.parent().children("input[type=checkbox]").attr("checked",true)});$(".head-check-item .fa-check-square-o").click(function(){var b=$(this);b.parent().removeClass("active");b.parent().children("input[type=checkbox]").attr("checked",false)});$(".selectall").click(function(){var b=$(this);b.parents(".tab-pane").find("input[type=checkbox]").attr("checked",true);b.parents(".tab-pane").find(".head-check-item").addClass("active")});$(".selectreverse").click(function(){var b=$(this);b.parents(".tab-pane").find("input[type=checkbox]").each(function(d,c){$(c).attr("checked",!$(c).attr("checked"))});b.parents(".tab-pane").find(".head-check-item").toggleClass("active")})});
